mapper.delete:
DELETE FROM COMTNFILEDETAIL
WHERE
	ATCH_FILE_ID = #{atchFileId}
AND
	FILE_SN = #{fileSn}

mapper.select:
SELECT IFNULL(MAX(FILE_SN),0)+1 AS FILE_SN
FROM COMTNFILEDETAIL
WHERE ATCH_FILE_ID =  #{atchFileId}

mapper.update:
UPDATE COMTNFILE
SET USE_AT = 'N'
WHERE ATCH_FILE_ID = #{atchFileId}

UPDATE COMTNFILE
SET USE_AT = 'Y'
WHERE ATCH_FILE_ID = #{atchFileId}

mapper.select:
SELECT a.ATCH_FILE_ID AS seleceKey, b.ORIGNL_FILE_NM AS selectValue FROM COMTNFILE a, COMTNFILEDETAIL b WHERE DATE_FORMAT(a.CREAT_DT, '%Y%m%d') = #{searchKeyword} AND b.FILE_GUBUN = #{fileGubun} AND a.ATCH_FILE_ID = b.ATCH_FILE_ID AND a.USE_AT = 'Y'
/* 협력사 조건 */ AND a.CREAT_ID IN ( SELECT emplyr_id FROM COMTNEMPLYRINFO WHERE IHIDNUM = #{schIhidNum} )
ORDER BY a.CREAT_DT DESC
