/* 협력사 조건 *//* COOPERATOR_ID로 조건을 만든건 ★정산 파일을 올릴때 권한이 있는 협력사ID 데이터만 올릴수 있음!!★ - 나머지는 오류 처리할꺼임 */ AND a.COOPERATOR_ID IN ( SELECT COOPERATOR_ID FROM RD_COOPERATOR a WHERE a.REGISTRATION_SN = #{schIhidNum} AND use_at ='Y' )
AND c.USE_AT ='Y'
AND a.COOPERATOR_ID = #{searchCooperatorId}
AND file_gubun = 'WEEK' ORDER BY c.CREAT_DT desc

SELECT c.ATCH_FILE_ID, d.ORIGNL_FILE_NM, a.COOPERATOR_ID, b.COOPERATOR_NM, COUNT(a.DELIVERY_SN) CNT, DATE_FORMAT(c.CREAT_DT,'%Y%m%d') CREAT_DT, d.ERORR_MSG FROM COMTNFILE c LEFT JOIN COMTNFILEDETAIL d ON c.ATCH_FILE_ID = d.ATCH_FILE_ID LEFT JOIN RD_DELIVERY_INFO a ON c.ATCH_FILE_ID = a.ATCH_FILE_ID LEFT JOIN RD_COOPERATOR b ON a.COOPERATOR_ID = b.COOPERATOR_ID WHERE DATE_FORMAT(c.CREAT_DT, '%Y%m') = #{searchDate} AND c.USE_AT ='Y'
/* 협력사 조건 *//* COOPERATOR_ID로 조건을 만든건 ★정산 파일을 올릴때 권한이 있는 협력사ID 데이터만 올릴수 있음!!★ - 나머지는 오류 처리할꺼임 */ AND a.COOPERATOR_ID IN ( SELECT COOPERATOR_ID FROM RD_COOPERATOR a WHERE a.REGISTRATION_SN = #{schIhidNum} AND use_at ='Y' )
/*AND b.USE_AT ='Y'*/
AND a.COOPERATOR_ID = #{searchCooperatorId}
AND file_gubun ='DAY' GROUP BY a.atch_file_id, b.COOPERATOR_ID, c.ATCH_FILE_ID ORDER BY c.CREAT_DT desc

SELECT COOPERATOR_ID as seleceKey, COOPERATOR_NM as selectValue FROM RD_COOPERATOR WHERE USE_AT ='Y'
/* 협력사 조건 */ AND COOPERATOR_ID IN ( SELECT COOPERATOR_ID FROM RD_COOPERATOR a WHERE a.REGISTRATION_SN = #{schIhidNum} AND use_at ='Y' )
/* 영업사원 조건 */ AND COOPERATOR_ID IN ( SELECT COOPERATOR_ID FROM RD_COOPERATOR_SALES WHERE EMPLYR_ID = #{searchId} AND USE_AT ='Y' )
UNION SELECT a.COOPERATOR_ID as seleceKey, CONCAT('미등록 / ',a.cooperator_id) as selectValue FROM RD_WEEK_INFO a LEFT JOIN RD_COOPERATOR b ON a.cooperator_id =b.cooperator_id WHERE (b.cooperator_id IS NULL or IFNULL(b.use_at, 'N') ='N') GROUP BY a.cooperator_id
UNION SELECT a.COOPERATOR_ID as seleceKey, CONCAT('미등록 / ',a.cooperator_id) as selectValue FROM RD_DELIVERY_INFO a LEFT JOIN RD_COOPERATOR b ON a.cooperator_id =b.cooperator_id WHERE (b.cooperator_id IS NULL or IFNULL(b.use_at, 'N') ='N') GROUP BY a.cooperator_id

SELECT tab.* , DELIVERY_PRICE/*배달비*/ - DAY_FEE /*선정산수수료+콜수수료*/ - INSURANCE /*4대보험*/ AS ABLE_PRICE FROM( SELECT COOPERATOR_ID, MBER_ID, DAY, '1' AS IO_GUBUN, DELIVERY_PRICE , DELIVERY_PRICE - INSURANCE - FEE_CALL_COST AS 산정대상 , CEILING((DELIVERY_PRICE - INSURANCE - FEE_CALL_COST) * FEE_ADMINSTRATOR) + FEE_CALL_COST AS DAY_FEE , INSURANCE , 'N' AS WEEK_YN, 'Y' AS USE_AT, ATCH_FILE_ID AS DAY_ATCH_FILE_ID, FEE_CALL_COST, DELIVERY_CNT, FEE_ID, RIDER_FEE_ID /*협력사 수익: 콜수수료*/ , FLOOR(FEE_CALL_COST*FEE_COOPERATOR_CALL) AS FEE_COOPERATOR_CALL_COST FROM ( SELECT fee_tab.* /*선지급수수료(%) + 건당 콜수수료(원) = 수익*/ /*, CEILING(DELIVERY_PRICE*FEE_ADMINSTRATOR) + DELIVERY_CNT*FEE_CALL AS DAY_FEE*/
/* 고용보험(%)+ 산재보험(%)+ 원천세(%) + 시간제보험(원 최대7,000) = 보험료 */ , CEILING(DELIVERY_PRICE*(FEE_EMPLOYMENT_INSURANCE+FEE_INDUSTRIAL_INSURANCE+FEE_WITHHOLDING_TAX)) + if(DELIVERY_CNT*FEE_TIME_INSURANCE > #{timeInsuranceMaxFee}, #{timeInsuranceMaxFee}, DELIVERY_CNT*FEE_TIME_INSURANCE) AS INSURANCE /* 콜수수료 */ , DELIVERY_CNT*FEE_CALL AS FEE_CALL_COST FROM ( SELECT nvl(b.FEE_ADMINSTRATOR*0.01, 0) AS FEE_ADMINSTRATOR /* 선지급수수료(%) */ , nvl(nvl(c.FEE_EMPLOYMENT_INSURANCE, b.FEE_EMPLOYMENT_INSURANCE)*0.01, 0) AS FEE_EMPLOYMENT_INSURANCE/* 고용보험(%) */ , nvl(nvl(c.FEE_INDUSTRIAL_INSURANCE, b.FEE_INDUSTRIAL_INSURANCE)*0.01, 0) AS FEE_INDUSTRIAL_INSURANCE/* 산재보험(%) */ , nvl(nvl(c.FEE_WITHHOLDING_TAX, b.FEE_WITHHOLDING_TAX)*0.01, 0) AS FEE_WITHHOLDING_TAX/* 원천세(%) */ , nvl(nvl(c.FEE_TIME_INSURANCE, b.FEE_TIME_INSURANCE), 0) AS FEE_TIME_INSURANCE/* 시간제보험(원 최대7,000) */ , nvl(nvl(c.FEE_CALL, b.FEE_CALL), 0) AS FEE_CALL/* 건당 콜수수료(원) */ , a.COOPERATOR_ID, a.MBER_ID,a.RUN_DE AS DAY, SUM(a.DELIVERY_PRICE) AS DELIVERY_PRICE, SUM(if(a.DELIVERY_STATE = TRIM('전달완료'), 1, 0)) DELIVERY_CNT , b.FEE_ID, c.RIDER_FEE_ID, b.FEE_COOPERATOR_CALL*0.01 AS FEE_COOPERATOR_CALL , a.ATCH_FILE_ID FROM RD_DELIVERY_INFO a LEFT JOIN RD_COOPERATOR_FEE b ON a.COOPERATOR_ID = b.COOPERATOR_ID AND b.use_at ='Y' LEFT JOIN RD_RIDER_FEE c ON a.COOPERATOR_ID = c.COOPERATOR_ID AND a.MBER_ID = c.MBER_ID AND c.use_at ='Y' WHERE ATCH_FILE_ID = #{dayAtchFileId} GROUP BY a.COOPERATOR_ID, a.MBER_ID, a.RUN_DE ) fee_tab
) sub_tab ) tab

SELECT tab.* , DELIVERY_PRICE/*배달비*/ - DAY_FEE /*선정산수수료+콜수수료+프로그램료*/ - INSURANCE /*4대보험*/ AS ABLE_PRICE FROM( SELECT COOPERATOR_ID, MBER_ID, ESNTL_ID, DAY, '1' AS IO_GUBUN, DELIVERY_PRICE , DELIVERY_PRICE - INSURANCE - FEE_CALL_COST - FEE_PROGRAM_COST AS 산정대상 , CEILING((DELIVERY_PRICE - INSURANCE - FEE_CALL_COST - FEE_PROGRAM_COST) * FEE_ADMINSTRATOR) + FEE_CALL_COST + FEE_PROGRAM_COST AS DAY_FEE , INSURANCE , 'N' AS WEEK_YN, 'Y' AS USE_AT, ATCH_FILE_ID AS DAY_ATCH_FILE_ID, FEE_CALL_COST, FEE_PROGRAM_COST, FEE_PROGRAM_SALESMAN_COST, DELIVERY_CNT, FEE_ID, RIDER_FEE_ID /*협력사 수익: 콜수수료*/ , FLOOR(FEE_CALL_COST*FEE_COOPERATOR_CALL) AS FEE_COOPERATOR_CALL_COST , COOPERATOR_NM, EMPLYR_ID, SALES_ESNTL_ID FROM ( SELECT fee_tab.* /*선지급수수료(%) + 건당 콜수수료(원) = 수익*/ /*, CEILING(DELIVERY_PRICE*FEE_ADMINSTRATOR) + DELIVERY_CNT*FEE_CALL AS DAY_FEE*/
/* 고용보험(%)+ 산재보험(%)+ 원천세(%) + 시간제보험(원 최대7,000) = 보험료 */ , CEILING(DELIVERY_PRICE*(FEE_EMPLOYMENT_INSURANCE+FEE_INDUSTRIAL_INSURANCE+FEE_WITHHOLDING_TAX)) + if(DELIVERY_CNT*FEE_TIME_INSURANCE > #{timeInsuranceMaxFee}, #{timeInsuranceMaxFee}, DELIVERY_CNT*FEE_TIME_INSURANCE) AS INSURANCE /* 콜수수료 */ , DELIVERY_CNT*FEE_CALL AS FEE_CALL_COST /* 전체 프로그램료 */ , DELIVERY_CNT*FEE_PROGRAM AS FEE_PROGRAM_COST /* 영업사원 프로그램료 */ , DELIVERY_CNT*FEE_PROGRAM_SALESMAN AS FEE_PROGRAM_SALESMAN_COST FROM ( SELECT nvl(b.FEE_ADMINSTRATOR*0.01, 0) AS FEE_ADMINSTRATOR /* 선지급수수료(%) */ , nvl(nvl(c.FEE_EMPLOYMENT_INSURANCE, b.FEE_EMPLOYMENT_INSURANCE)*0.01, 0) AS FEE_EMPLOYMENT_INSURANCE/* 고용보험(%) */ , nvl(nvl(c.FEE_INDUSTRIAL_INSURANCE, b.FEE_INDUSTRIAL_INSURANCE)*0.01, 0) AS FEE_INDUSTRIAL_INSURANCE/* 산재보험(%) */ , nvl(nvl(c.FEE_WITHHOLDING_TAX, b.FEE_WITHHOLDING_TAX)*0.01, 0) AS FEE_WITHHOLDING_TAX/* 원천세(%) */ , nvl(nvl(c.FEE_TIME_INSURANCE, b.FEE_TIME_INSURANCE), 0) AS FEE_TIME_INSURANCE/* 시간제보험(원 최대7,000) */ , nvl(nvl(c.FEE_CALL, b.FEE_CALL), 0) AS FEE_CALL/* 건당 콜수수료(원) */ , nvl(b.FEE_PROGRAM, 0) AS FEE_PROGRAM /* 전체 프로그램료(원) */ , nvl(b.FEE_PROGRAM_SALESMAN, 0) AS FEE_PROGRAM_SALESMAN /* 영업사원 프로그램료(원) */ , a.COOPERATOR_ID, a.MBER_ID,a.RUN_DE AS DAY, SUM(a.DELIVERY_PRICE) AS DELIVERY_PRICE, SUM(if(a.DELIVERY_STATE = TRIM('전달완료'), 1, 0)) DELIVERY_CNT , b.FEE_ID, c.RIDER_FEE_ID, b.FEE_COOPERATOR_CALL*0.01 AS FEE_COOPERATOR_CALL , a.ATCH_FILE_ID, d.ESNTL_ID, f.COOPERATOR_NM, g.EMPLYR_ID, h.ESNTL_ID AS SALES_ESNTL_ID FROM RD_DELIVERY_INFO a LEFT JOIN RD_COOPERATOR_FEE b ON a.COOPERATOR_ID = b.COOPERATOR_ID AND b.use_at ='Y' LEFT JOIN RD_RIDER_FEE c ON a.COOPERATOR_ID = c.COOPERATOR_ID AND a.MBER_ID = c.MBER_ID AND c.use_at ='Y' LEFT JOIN COMTNGNRLMBER d ON a.mber_id = d.mber_id JOIN COMTNFILE e ON a.ATCH_FILE_ID = e.ATCH_FILE_ID LEFT JOIN RD_COOPERATOR f ON a.COOPERATOR_ID = f.COOPERATOR_ID LEFT JOIN RD_COOPERATOR_SALES g ON a.COOPERATOR_ID = g.COOPERATOR_ID AND g.use_AT ='Y' LEFT JOIN COMTNEMPLYRINFO h ON g.EMPLYR_ID = h.EMPLYR_ID WHERE 1 = 1 AND e.USE_AT ='Y' AND a.RUN_DE BETWEEN #{searchFromDate} AND #{searchToDate}
AND d.MBER_NM like '%' #{searchNm} '%'
AND a.FIX_DAY is null /* 확정은 미확정 데이터를 대상으로만 한다 */ GROUP BY a.COOPERATOR_ID, a.MBER_ID, a.RUN_DE ) fee_tab
) sub_tab ) tab

