SELECT b.WEEK_ID,b.NO,a.cooperator_id,b.MBER_ID,b.MBER_NM,b.CNT,b.DELIVERY_COST,b.ADD_COST,b.SUM_COST,b.TIME_INSURANCE,b.NECESSARY_EXPENSES,b.PAY,b.OWNER_EMPLOYMENT_INSURANCE ,b.RIDER_EMPLOYMENT_INSURANCE,b.OWNER_INDUSTRIAL_INSURANCE,b.RIDER_INDUSTRIAL_INSURANCE,b.WITHHOLDING_TAX_INSURANCE_SUM ,b.OWNER_EMPLOYMENT_INSURANCE_ACCOUNTS,b.RIDER_EMPLOYMENT_INSURANCE_ACCOUNTS,b.SUM_EMPLOYMENT_INSURANCE_ACCOUNTS ,b.OWNER_INDUSTRIAL_INSURANCE_ACCOUNTS,b.RIDER_INDUSTRIAL_INSURANCE_ACCOUNTS,b.SUM_INDUSTRIAL_INSURANCE_ACCOUNTS ,b.OPERATING_COST,b.ACCOUNTS_COST,b.INCOME_TAX,b.RESIDENCE_TAX,b.WITHHOLDING_TAX,b.GIVE_PAY,b.CREAT_DT,b.CREAT_ID ,b.OPERATING_COST_ADD FROM RD_WEEK_INFO_OUT a LEFT JOIN RD_WEEK_RIDER_INFO_OUT b on a.week_id = b.week_id LEFT JOIN COMTNGNRLMBER c ON b.MBER_ID = c.MBER_ID JOIN COMTNFILE d ON a.ATCH_FILE_ID = d.ATCH_FILE_ID WHERE 1 = 1 AND d.USE_AT = 'Y' AND a.ATCH_FILE_ID = #{searchAtchFileId}
/* 협력사 조건 *//* COOPERATOR_ID로 조건을 만든건 ★정산 파일을 올릴때 권한이 있는 협력사ID 데이터만 올릴수 있음!!★ - 나머지는 오류 처리할꺼임 */ AND COOPERATOR_ID IN ( SELECT COOPERATOR_ID FROM RD_COOPERATOR a WHERE a.REGISTRATION_SN = #{schIhidNum} AND use_at ='Y' )
order by b.WEEK_ID asc, LPAD(b.NO, 4,0) ASC
