mapper.select:
SELECT
	COOPERATOR_ID, COOPERATOR_NM,REGISTRATION_SN,REGISTRATION_NM,RUN_DE,DELIVERY_SN,DELIVERY_STATE,
	SERVICE_TYPE,DELIVERY_TYPE,RIDER_ID,a.MBER_ID,RIDER_NM,DELIVERY_METHOD,SHOP_SN,SHOP_NM,GOODS_PRICE,
	PICKUP_ADDR,DESTINATION_ADDR,ORDER_DT,OPERATE_RIDER_DT,SHOP_COMEIN_DT,PICKUP_FINIST_DT,DELIVERY_FINIST_DT,
	DISTANCE,ADD_DELIVERY_REASON,ADD_DELIVERY_DESC,PICKUP_LAW_DONG,BASIC_PRICE,WEATHER_PRIMAGE,ADD_PRIMAGE,
	PEAK_PRIMAGE_ETC,DELIVERY_PRICE,RIDER_CAUSE_YN,ADD_PRIMAGE_DESC,NOTE,ATCH_FILE_ID,CREAT_DT,CREAT_ID
FROM RD_DELIVERY_INFO a
LEFT JOIN COMTNGNRLMBER b ON a.mber_id = b.mber_id
WHERE 1=1

		AND ATCH_FILE_ID = #{searchId}


		AND a.MBER_ID = #{searchMberId}


		AND a.RUN_DE like '%' #{searchRunDeDate} '%'




		AND COOPERATOR_ID = #{searchCooperatorId}


		AND MBER_NM like '%' #{searchNm} '%'

		AND RUN_DE BETWEEN #{searchFromDate} AND #{searchToDate}

		AND COOPERATOR_ID IN (
			SELECT COOPERATOR_ID from RD_COOPERATOR
			WHERE REGISTRATION_SN = #{searchRegistrationSn}
		)




	/* 협력사 조건 *//* COOPERATOR_ID로 조건을 만든건 ★정산 파일을 올릴때 권한이 있는 협력사ID 데이터만 올릴수 있음!!★ - 나머지는 오류 처리할꺼임  */
	AND COOPERATOR_ID IN (
		SELECT COOPERATOR_ID FROM RD_COOPERATOR a
		WHERE a.REGISTRATION_SN = #{schIhidNum} AND use_at ='Y'
	)

SELECT b.WEEK_ID,b.NO,b.MBER_ID,b.MBER_NM,b.CNT,b.DELIVERY_COST,b.ADD_COST,b.SUM_COST,b.TIME_INSURANCE,b.NECESSARY_EXPENSES,b.PAY,b.OWNER_EMPLOYMENT_INSURANCE ,b.RIDER_EMPLOYMENT_INSURANCE,b.OWNER_INDUSTRIAL_INSURANCE,b.RIDER_INDUSTRIAL_INSURANCE,b.WITHHOLDING_TAX_INSURANCE_SUM ,b.OWNER_EMPLOYMENT_INSURANCE_ACCOUNTS,b.RIDER_EMPLOYMENT_INSURANCE_ACCOUNTS,b.SUM_EMPLOYMENT_INSURANCE_ACCOUNTS ,b.OWNER_INDUSTRIAL_INSURANCE_ACCOUNTS,b.RIDER_INDUSTRIAL_INSURANCE_ACCOUNTS,b.SUM_INDUSTRIAL_INSURANCE_ACCOUNTS ,b.OPERATING_COST,b.ACCOUNTS_COST,b.INCOME_TAX,b.RESIDENCE_TAX,b.WITHHOLDING_TAX,b.GIVE_PAY,b.CREAT_DT,b.CREAT_ID FROM RD_WEEK_INFO a, RD_WEEK_RIDER_INFO b WHERE a.week_id = b.week_id AND ATCH_FILE_ID = #{searchAtchFileId}
AND b.MBER_ID = #{searchMberId}
/* 협력사 조건 *//* COOPERATOR_ID로 조건을 만든건 ★정산 파일을 올릴때 권한이 있는 협력사ID 데이터만 올릴수 있음!!★ - 나머지는 오류 처리할꺼임 */ AND COOPERATOR_ID IN ( SELECT COOPERATOR_ID FROM RD_COOPERATOR a WHERE a.REGISTRATION_SN = #{schIhidNum} AND use_at ='Y' )
order by b.WEEK_ID asc, LPAD(b.NO, 4,0) ASC

SELECT c.ATCH_FILE_ID, d.ORIGNL_FILE_NM, a.COOPERATOR_ID, b.COOPERATOR_NM, a.WEEK_ID, a.ACCOUNTS_ST_DT, a.ACCOUNTS_ED_DT, DATE_FORMAT(c.CREAT_DT,'%Y%m%d') CREAT_DT
FROM RD_WEEK_INFO a
	left JOIN RD_COOPERATOR b ON a.COOPERATOR_ID = b.COOPERATOR_ID
	LEFT JOIN COMTNFILE c ON a.atch_file_id  =c.ATCH_FILE_ID
	LEFT JOIN COMTNFILEDETAIL d ON c.ATCH_FILE_ID = d.ATCH_FILE_ID
WHERE SUBSTRING(a.ACCOUNTS_ST_DT, 1, 6) = #{searchDate}


	/* 협력사 조건 *//* COOPERATOR_ID로 조건을 만든건 ★정산 파일을 올릴때 권한이 있는 협력사ID 데이터만 올릴수 있음!!★ - 나머지는 오류 처리할꺼임  */
	AND a.COOPERATOR_ID IN (
		SELECT COOPERATOR_ID FROM RD_COOPERATOR a
		WHERE a.REGISTRATION_SN = #{schIhidNum} AND use_at ='Y'
	)


	/*AND b.USE_AT ='Y'*/

		AND	a.COOPERATOR_ID = #{searchCooperatorId}

SELECT c.ATCH_FILE_ID, d.ORIGNL_FILE_NM, a.COOPERATOR_ID, b.COOPERATOR_NM, COUNT(a.DELIVERY_SN) CNT, DATE_FORMAT(a.CREAT_DT,'%Y%m%d') CREAT_DT
FROM RD_DELIVERY_INFO a
	LEFT JOIN RD_COOPERATOR b ON a.COOPERATOR_ID = b.COOPERATOR_ID
	LEFT JOIN COMTNFILE c ON a.atch_file_id  =c.ATCH_FILE_ID
	LEFT JOIN COMTNFILEDETAIL d ON c.ATCH_FILE_ID = d.ATCH_FILE_ID
WHERE SUBSTRING(a.RUN_DE, 1, 6) = #{searchDate}


	/* 협력사 조건 *//* COOPERATOR_ID로 조건을 만든건 ★정산 파일을 올릴때 권한이 있는 협력사ID 데이터만 올릴수 있음!!★ - 나머지는 오류 처리할꺼임  */
	AND a.COOPERATOR_ID IN (
		SELECT COOPERATOR_ID FROM RD_COOPERATOR a
		WHERE a.REGISTRATION_SN = #{schIhidNum} AND use_at ='Y'
	)


	/*AND b.USE_AT ='Y'*/

		AND	a.COOPERATOR_ID = #{searchCooperatorId}

GROUP BY a.atch_file_id, b.COOPERATOR_ID

mapper.update:
UPDATE RD_DAY_PAY
SET WEEK_YN ='Y'
, WEK_ATCH_FILE_ID = #{wekAtchFileId}
, WEEK_ID = #{weekId}
,LAST_UPDT_PNTTM = SYSDATE()
, LAST_UPDUSR_ID = #{lastUpdusrId}
WHERE WEEK_YN ='N'
AND USE_AT ='Y'
AND IO_GUBUN = '1'
AND COOPERATOR_ID = #{cooperatorId}
AND DAY BETWEEN #{accountsStDt} AND #{accountsEdDt}

UPDATE RD_DAY_PAY
SET WEEK_YN ='Y'
, WEK_ATCH_FILE_ID = #{wekAtchFileId}
, WEEK_ID = #{weekId}
,LAST_UPDT_PNTTM = SYSDATE()
, LAST_UPDUSR_ID = #{lastUpdusrId}
WHERE WEEK_YN ='N'
AND USE_AT ='Y'
AND IO_GUBUN = '2'
AND COOPERATOR_ID = #{cooperatorId}

UPDATE RD_TRANSFER
SET STATUS = #{status}

	, STATUS_CD = #{statusCd}


	, SEND_DT = #{sendDt}


	, SEND_TM = #{sendTm}


	, NATV_TR_NO = #{natvTrNo}


	, ERROR_CODE = #{errorCode}


	, ERROR_MESSAGE = #{errorMessage}

WHERE TRAN_DAY = #{tranDay} AND TELEGRAM_NO = #{telegram_no}

UPDATE RD_SCH_ACCOUNT
SET
	 STATUS = #{status}

	, STATUS_CD = #{statusCd}


	, SEND_DT = #{sendDt}


	, SEND_TM = #{sendTm}


	, NATV_TR_NO = #{natvTrNo}


	, DEPOSITOR = #{depositor}


	, ERROR_CODE = #{errorCode}


	, ERROR_MESSAGE = #{errorMessage}

WHERE TRAN_DAY = #{tranDay} AND TELEGRAM_NO = #{telegram_no}

mapper.select:
SELECT * FROM( SELECT COOPERATOR_ID,'DAY' AS DW_GUBUN,'선정산' AS GUBUN, DYP_ID AS ID, a.SEND_PRICE, a.DAY_FEE+a.INSURANCE+a.SEND_FEE AS FEE , b.TRAN_DAY , b.RV_ACCOUNT, b.STATUS, b.STATUS_cd, b.error_code , case when b.STATUS='200' then '이체' when b.STATUS= '400' AND b.error_code='VTIM' then '처리중' when b.STATUS= '400' AND b.error_code='0011' then '처리중' ELSE '실패' end AS STATUS_NM , b.SEND_DT, b.SEND_TM, b.CREAT_DT, b.error_message FROM RD_DAY_PAY a LEFT JOIN RD_TRANSFER b ON a.TRAN_DAY = b.TRAN_DAY AND a.TELEGRAM_NO = b.TELEGRAM_NO WHERE week_yn ='N' AND IO_GUBUN ='2' AND COOPERATOR_ID = #{searchCooperatorId} AND a.ETC_ID IS null /*대출 제외*/ AND MBER_ID = #{schId} AND a.TRAN_DAY BETWEEN #{searchFromDate} AND #{searchToDate}
UNION all
SELECT COOPERATOR_ID, DW_GUBUN, if(DW_GUBUN='DAY', '선정산', '정산') AS GUBUN, WKP_ID AS ID, a.SEND_PRICE, a.FEE , b.TRAN_DAY , b.RV_ACCOUNT, b.STATUS, b.STATUS_cd, b.error_code , case when b.STATUS='200' then '이체' when b.STATUS= '400' AND b.error_code='VTIM' then '처리중' when b.STATUS= '400' AND b.error_code='0011' then '처리중' ELSE '실패' end AS STATUS_NM , b.SEND_DT, b.SEND_TM, b.CREAT_DT, b.error_message FROM RD_WEEK_PAY a LEFT JOIN RD_TRANSFER b ON a.tran_day = b.tran_day AND a.TELEGRAM_NO = b.TELEGRAM_NO WHERE a.USE_AT ='Y' AND IO_GUBUN ='2' AND COOPERATOR_ID = #{searchCooperatorId} AND a.ETC_ID IS null /*대출 제외*/ AND MBER_ID = #{schId} AND a.TRAN_DAY BETWEEN #{searchFromDate} AND #{searchToDate} ) tab ORDER BY creat_dt desc

mapper.update:
UPDATE RD_ETC
SET FINISH_AT = 'Y'
, LAST_UPDT_PNTTM = SYSDATE()
, LAST_UPDUSR_ID = #{lastUpdusrId}
WHERE etc_id = #{etcId}
AND 'true' = (
	SELECT if(payback_cost_all <= send_price, 'true', 'false')
	FROM RD_ETC a,
	(
		SELECT NVL(SUM(send_price), 0)  as send_price
		FROM(
			SELECT send_price FROM RD_DAY_PAY WHERE etc_id = #{etcId} AND week_yn !='Y'/* 정산완료된 일정산건은 주정산으로 이동되었으니 제외*/
			UNION ALL
			SELECT send_price FROM RD_WEEK_PAY WHERE etc_id = #{etcId}
		) c
	) b
	WHERE etc_id = #{etcId}
)
