mapper.select:
select COOPERATOR_ID,COOPERATOR_NM, REGISTRATION_SN, COMPANY_NM, REGISTRATION_NM, CEO_NM, USE_AT, CREAT_DT, CREAT_ID, LAST_UPDT_PNTTM, LAST_UPDUSR_ID
from RD_COOPERATOR
where COOPERATOR_ID = #{cooperatorId}

mapper.update:
UPDATE RD_COOPERATOR
SET
COOPERATOR_NM = #{cooperatorNm}
, REGISTRATION_SN=REGEXP_REPLACE(#{registrationSn}, '[^0-9]+', '')
, COMPANY_NM=#{companyNm}

/* 총판만 사업자번호 변경이 가능 */
, REGISTRATION_NM = #{registrationNm}

, CEO_NM = #{ceoNm}

/* 총판만 사용여부 변경이 가능 */
, USE_AT = #{useAt}

, LAST_UPDT_PNTTM = sysdate()
, LAST_UPDUSR_ID = #{lastUpdusrId}
WHERE COOPERATOR_ID = #{cooperatorId}

mapper.select:
SELECT COOPERATOR_ID, MBER_ID, USE_AT , CREAT_DT, CREAT_ID, LAST_UPDT_PNTTM , LAST_UPDUSR_ID
FROM RD_COOPERATOR_USR
WHERE mber_id = #{mberId}

mapper.update:
UPDATE RD_COOPERATOR_USR
SET USE_AT = #{useAt}, LAST_UPDT_PNTTM = sysdate(), LAST_UPDUSR_ID = #{lastUpdusrId}
WHERE COOPERATOR_ID = #{mberId}

mapper.select:
SELECT cte.con_id, cte.COOPERATOR_ID, cte.MBER_ID, cte.USE_AT, cte.REG_DT, cte.END_DT , usr.mber_nm, usr.MBTLNUM , NVL(fee.FEE_EMPLOYMENT_INSURANCE, b.FEE_EMPLOYMENT_INSURANCE) AS FEE_EMPLOYMENT_INSURANCE , NVL(fee.FEE_INDUSTRIAL_INSURANCE, b.FEE_INDUSTRIAL_INSURANCE) AS FEE_INDUSTRIAL_INSURANCE , NVL(fee.FEE_WITHHOLDING_TAX, b.FEE_WITHHOLDING_TAX) AS FEE_WITHHOLDING_TAX , NVL(fee.FEE_TIME_INSURANCE, b.FEE_TIME_INSURANCE) AS FEE_TIME_INSURANCE , NVL(fee.FEE_CALL, b.FEE_CALL) AS FEE_CALL
FROM RD_COOPERATOR_RIDER_CONNECT cte LEFT JOIN COMTNGNRLMBER usr ON cte.mber_id = usr.mber_id LEFT JOIN RD_RIDER_FEE fee ON cte.COOPERATOR_ID = fee.COOPERATOR_ID AND cte.MBER_ID = fee.MBER_ID AND fee.USE_AT ='Y' LEFT JOIN RD_COOPERATOR_FEE b ON cte.COOPERATOR_ID = b.COOPERATOR_ID AND b.USE_AT = 'Y'
WHERE 1=1 AND cte.COOPERATOR_ID = #{cooperatorId}
/* 협력사 조건 */ AND cte.COOPERATOR_ID IN ( SELECT COOPERATOR_ID FROM RD_COOPERATOR a WHERE a.REGISTRATION_SN = #{schIhidNum} AND use_at ='Y' )
/*WITH RECURSIVE cte AS ( SELECT 1 as lv, CON_ID, UP_CON_ID , COOPERATOR_ID, MBER_ID, use_at FROM RD_COOPERATOR_RIDER_CONNECT WHERE UP_CON_ID is null
UNION ALL
SELECT 1+cte.lv lv, m.CON_ID, m.UP_CON_ID , m.COOPERATOR_ID, m.MBER_ID, m.use_at FROM RD_COOPERATOR_RIDER_CONNECT m INNER JOIN cte ON m.UP_CON_ID = cte.con_id
)*/ /*WHERE (cte.lv , cte.COOPERATOR_ID, cte.MBER_ID) IN ( SELECT MAX(lv) lv, COOPERATOR_ID, MBER_ID FROM cte WHERE COOPERATOR_ID = #{cooperatorId} GROUP BY MBER_ID )*/ ORDER BY cte.CREAT_DT

SELECT CON_ID/*, UP_CON_ID*/,COOPERATOR_ID, MBER_ID, CREAT_DT, CREAT_ID
FROM RD_COOPERATOR_RIDER_CONNECT
WHERE con_id = #{conId}

mapper.update:
UPDATE RD_COOPERATOR_RIDER_CONNECT
SET USE_AT = #{useAt}

	, REG_DT = if(REG_DT is null OR REG_DT = '', DATE_FORMAT(SYSDATE(), '%Y%m%d') , REG_DT)

	, END_DT = DATE_FORMAT(if(#{endDt} = '', NULL, #{endDt}) , '%Y%m%d')
	, LAST_UPDT_PNTTM = sysdate()
	, LAST_UPDUSR_ID = #{lastUpdusrId}
WHERE CON_ID = #{conId}

UPDATE RD_COOPERATOR_FEE
SET END_DT = DATE_FORMAT(#{endDt}, '%Y%m%d')
	, LAST_UPDT_PNTTM = sysdate()
	, LAST_UPDUSR_ID = #{lastUpdusrId}
WHERE fee_id = #{feeId}

/* 협력사 조건 */
AND COOPERATOR_ID IN (
	SELECT COOPERATOR_ID FROM RD_COOPERATOR a
	WHERE a.REGISTRATION_SN = #{schIhidNum} AND use_at ='Y'
)

UPDATE RD_RIDER_FEE
SET USE_AT = 'N'
	, LAST_UPDT_PNTTM = sysdate()
	, LAST_UPDUSR_ID = #{lastUpdusrId}
WHERE COOPERATOR_ID = #{cooperatorId}
	and MBER_ID =  #{mberId}
	and USE_AT = 'Y'

mapper.select:
select a.FEE_ID from RD_COOPERATOR_FEE a
WHERE a.COOPERATOR_ID = #{cooperatorId} AND use_at ='Y'
AND a.FEE_ADMINSTRATOR = #{feeAdminstrator}
AND a.FEE_COOPERATOR = #{feeCooperator}
AND a.FEE_EMPLOYMENT_INSURANCE = #{feeEmploymentInsurance}
AND a.FEE_INDUSTRIAL_INSURANCE = #{feeIndustrialInsurance}
AND a.FEE_WITHHOLDING_TAX = #{feeWithholdingTax}
AND a.FEE_TIME_INSURANCE = #{feeTimeInsurance}
AND a.FEE_CALL = #{feeCall}
AND a.FEE_COOPERATOR_CALL = #{feeCooperatorCall}
AND a.FEE_PROGRAM = #{feeProgram}

select a.RIDER_FEE_ID from RD_RIDER_FEE a
WHERE a.COOPERATOR_ID = #{cooperatorId} AND use_at ='Y' AND a.MBER_ID = #{mberId}
AND a.FEE_EMPLOYMENT_INSURANCE = #{feeEmploymentInsurance}
AND a.FEE_INDUSTRIAL_INSURANCE = #{feeIndustrialInsurance}
AND a.FEE_WITHHOLDING_TAX = #{feeWithholdingTax}
AND a.FEE_TIME_INSURANCE = #{feeTimeInsurance}
AND a.FEE_CALL = #{feeCall}

mapper.insert:
INSERT INTO RD_COOPERATOR_FEE
(FEE_ID, COOPERATOR_ID
, FEE_ADMINSTRATOR, FEE_COOPERATOR, FEE_EMPLOYMENT_INSURANCE, FEE_INDUSTRIAL_INSURANCE
, FEE_WITHHOLDING_TAX, FEE_TIME_INSURANCE, FEE_CALL, FEE_COOPERATOR_CALL, FEE_PROGRAM
, USE_AT, CREAT_DT, CREAT_ID)
SELECT #{feeId}, #{cooperatorId}
, #{feeAdminstrator}, #{feeCooperator}, #{feeEmploymentInsurance}, #{feeIndustrialInsurance}
, #{feeWithholdingTax}, #{feeTimeInsurance}, #{feeCall}, #{feeCooperatorCall}, #{feeProgram}
, 'Y', SYSDATE(), #{creatId}

/* 협력사 조건 */
FROM 	RD_COOPERATOR a
WHERE a.REGISTRATION_SN = #{schIhidNum} AND use_at ='Y'
AND a.COOPERATOR_ID = #{cooperatorId}

INSERT INTO RD_RIDER_FEE
(RIDER_FEE_ID, COOPERATOR_ID, MBER_ID
, FEE_EMPLOYMENT_INSURANCE, FEE_INDUSTRIAL_INSURANCE
, FEE_WITHHOLDING_TAX, FEE_TIME_INSURANCE, FEE_CALL
, USE_AT, CREAT_DT, CREAT_ID)
SELECT #{riderFeeId}, #{cooperatorId}, #{mberId}
, #{feeEmploymentInsurance}, #{feeIndustrialInsurance}
, #{feeWithholdingTax}, #{feeTimeInsurance}, #{feeCall}
, 'Y', SYSDATE(), #{creatId}

/* 협력사 조건 */
FROM 	RD_COOPERATOR a
WHERE a.REGISTRATION_SN = #{schIhidNum} AND use_at ='Y'
AND a.COOPERATOR_ID = #{cooperatorId}

mapper.select:
SELECT * FROM ( SELECT ROWNUM() RN,m.* FROM (
SELECT @ROWNUM:=@ROWNUM+1 AS UNIQ, a.ETC_ID, a.COOPERATOR_ID, a.MBER_ID, a.GUBUN, a.PAYBACK_DAY, a.PAYBACK_COST, a.PAYBACK_COST_ALL, DATE_FORMAT(a.AUTH_REQUEST_DT, '%Y%m%d') AUTH_REQUEST_DT , DATE_FORMAT(a.AUTH_RESPONS_DT, '%Y%m%d') AUTH_RESPONS_DT, a.RESPONS_AT, a.FINISH_AT, a.USE_AT, DATE_FORMAT(a.CREAT_DT, '%Y%m%d') CREAT_DT, a.CREAT_ID, a.LAST_UPDT_PNTTM, a.LAST_UPDUSR_ID , 'false' chk, nvl(SUM(b.SEND_PRICE), 0) + nvl(SUM(c.SEND_PRICE), 0) AS FINISH_COST , d.MBER_NM, e.COOPERATOR_NM FROM RD_ETC a LEFT JOIN RD_DAY_PAY b ON a.COOPERATOR_ID = b.COOPERATOR_ID AND a.MBER_ID = b.MBER_ID AND a.ETC_ID = b.ETC_ID and b.USE_AT = 'Y' AND b.WEEK_YN !='Y' LEFT JOIN RD_WEEK_PAY c ON a.COOPERATOR_ID = c.COOPERATOR_ID AND a.MBER_ID = c.MBER_ID AND a.ETC_ID = c.ETC_ID AND c.USE_AT = 'Y' LEFT JOIN COMTNGNRLMBER d ON a.mber_id = d.mber_id LEFT JOIN RD_COOPERATOR e ON a.COOPERATOR_ID = e.COOPERATOR_ID , (SELECT @ROWNUM:=0) AS R WHERE 1=1 AND a.use_at ='Y'
AND a.COOPERATOR_ID = #{cooperatorId}
AND a.MBER_ID = #{mberId}
AND a.auth_request_dt IS NOT NULL AND a.auth_respons_dt IS NULL /*요청했으나 승인을 않한건*/
AND a.auth_request_dt IS NOT NULL
AND a.FINISH_AT = 'Y'
AND a.FINISH_AT = 'N' and a.AUTH_RESPONS_DT is not null
AND a.AUTH_RESPONS_DT is null
AND a.cooperator_id = #{searchCooperatorId}
AND d.mber_nm like '%' #{searchNm} '%'
AND DATE_FORMAT(a.CREAT_DT,'%Y%m%d') BETWEEN #{searchFromDate} AND #{searchToDate}
AND a.COOPERATOR_ID IN ( SELECT COOPERATOR_ID from rd_cooperator WHERE REGISTRATION_SN = #{searchRegistrationSn} )
/* 협력사 조건 */ AND a.COOPERATOR_ID IN ( SELECT COOPERATOR_ID FROM RD_COOPERATOR a WHERE a.REGISTRATION_SN = #{schIhidNum} AND use_at ='Y' )
GROUP BY a.etc_id
) m ) m WHERE RN > #{schIdx}*#{schPagePerCnt} -#{schPagePerCnt} AND RN <= #{schIdx}*#{schPagePerCnt}

mapper.insert:
INSERT INTO RD_ETC
(ETC_ID, COOPERATOR_ID, MBER_ID, GUBUN, PAYBACK_DAY, PAYBACK_COST, PAYBACK_COST_ALL
, USE_AT, CREAT_DT, CREAT_ID

, AUTH_REQUEST_DT

)
SELECT #{etcId}, #{cooperatorId}, #{mberId}, #{gubun}, #{paybackDay}, #{paybackCost}, #{paybackCostAll}
, 'Y', SYSDATE(), #{creatId}

, SYSDATE()


/* 협력사 조건 */
FROM 	RD_COOPERATOR a
WHERE a.REGISTRATION_SN = #{schIhidNum} AND use_at ='Y'
AND a.COOPERATOR_ID = #{cooperatorId}

mapper.update:
UPDATE RD_ETC
SET GUBUN = #{gubun}, PAYBACK_DAY = #{paybackDay}, PAYBACK_COST = #{paybackCost}, PAYBACK_COST_ALL = #{paybackCostAll}
	, LAST_UPDT_PNTTM = #{lastUpdtPnttm}, LAST_UPDUSR_ID = SYSDATE()
WHERE ETC_ID = #{etcId}

mapper.select:
SELECT * FROM ( SELECT @ROWNUM:=@ROWNUM+1 AS RNUM, order_tab.* FROM( SELECT a.day, a.send_price, a.etc_id FROM RD_DAY_PAY a, (SELECT @ROWNUM:=0) AS R WHERE IO_GUBUN = '2' AND WEEK_YN ='N' AND a.USE_AT = 'Y' AND ETC_ID = #{etcId}
UNION ALL
SELECT a.TRAN_DAY AS day, a.send_price, a.etc_id FROM RD_WEEK_PAY a, (SELECT @ROWNUM:=0) AS R WHERE IO_GUBUN = '2' AND a.USE_AT = 'Y' AND ETC_ID = #{etcId} ) order_tab ORDER BY DAY ASC ) sub_tab ORDER BY rnum desc
