mapper.select:

SELECT * FROM (
	SELECT ROWNUM() RN,m.*
	FROM (

		SELECT a.COOPERATOR_ID, a.ESNTL_ID, a.DAY

			, if(a.BALANCE1 <0 , BALANCE0+BALANCE1, BALANCE0) AS BALANCE0 , if(a.BALANCE1 <0 , 0, BALANCE1) AS BALANCE1
			, if(a.ABLE_BALANCE1 <0 , ABLE_BALANCE0+ABLE_BALANCE1, ABLE_BALANCE0) AS ABLE_BALANCE0 , if(a.ABLE_BALANCE1 <0 , 0, ABLE_BALANCE1) AS ABLE_BALANCE1

			, if(a.COOPERATOR_ID = #{cooperatorMberId}, d.USER_NM,b.MBER_NM) AS MBER_NM, c.COOPERATOR_NM
			, if(a.COOPERATOR_ID = #{cooperatorMberId}, d.EMPLYR_ID,b.mber_id) AS mber_id
			, if(a.ESNTL_ID = #{cooperatorMberId}, '협력사', if(a.COOPERATOR_ID = #{cooperatorMberId}, '영업사원','라이더')) AS GUBUN_NM

		FROM RD_BALANCE_CONFIRM a
		LEFT JOIN COMTNGNRLMBER b ON a.ESNTL_ID = b.ESNTL_ID
		LEFT JOIN RD_COOPERATOR c ON a.COOPERATOR_ID = c.COOPERATOR_ID
		LEFT JOIN COMTNEMPLYRINFO d ON a.ESNTL_ID = d.ESNTL_ID
		WHERE DAY BETWEEN #{searchFromDate} AND #{searchToDate}

			AND a.ESNTL_ID != #{cooperatorMberId} AND a.COOPERATOR_ID != #{cooperatorMberId}


			AND a.COOPERATOR_ID = #{cooperatorMberId}


			AND a.ESNTL_ID = #{cooperatorMberId}



	) m
) m
WHERE RN > #{schIdx}*#{schPagePerCnt} -#{schPagePerCnt} AND RN <= #{schIdx}*#{schPagePerCnt}

SELECT COUNT(COOPERATOR_ID) AS TOTAL_CNT
FROM RD_BALANCE_CONFIRM
WHERE DAY BETWEEN #{searchFromDate} AND #{searchToDate}

	AND ESNTL_ID != #{cooperatorMberId}


	AND ESNTL_ID = #{cooperatorMberId}

-- 콜수수료, 기타수수료, 선지급수수료(부가세, 원천세, 관리비, 사업주부담고용보험료, 사업주부담산재보험료 제외) SELECT NVL(SUM(b.COST), 0) AS COST, fix.ACCOUNTS_ED_DT AS DAY, c.COOPERATOR_NM, fix.COOPERATOR_ID FROM ( SELECT sub_tab.ACCOUNTS_ED_DT , b.COOPERATOR_ID FROM (SELECT ACCOUNTS_ED_DT FROM RD_WEEK_INFO WHERE FIX_DAY IS NOT NULL GROUP BY ACCOUNTS_ED_DT desc LIMIT 7) sub_tab -- 최근 7주만 조회 LEFT JOIN RD_COOPERATOR b ON b.USE_AT ='Y' ) AS fix LEFT JOIN RD_WEEK_INFO a ON fix.COOPERATOR_ID = a.COOPERATOR_ID AND fix.ACCOUNTS_ED_DT = a.ACCOUNTS_ED_DT AND a.FIX_DAY IS NOT NULL LEFT JOIN RD_COOPERATOR_PROFIT b ON a.WEEK_ID = b.WEEK_ID AND b.USE_AT ='Y' AND b.GUBUN IN ('C','E','D') LEFT JOIN RD_COOPERATOR c ON fix.COOPERATOR_ID = c.COOPERATOR_ID where 1 = 1
/* 협력사 조건 */ AND c.REGISTRATION_SN = #{schIhidNum} AND c.use_at ='Y'
GROUP BY fix.ACCOUNTS_ED_DT, fix.COOPERATOR_ID ORDER BY fix.ACCOUNTS_ED_DT

-- 배달건수 SELECT NVL(SUM(b.cnt), 0) AS cnt, fix.ACCOUNTS_ED_DT AS DAY, c.COOPERATOR_NM, fix.COOPERATOR_ID FROM ( SELECT sub_tab.ACCOUNTS_ED_DT , b.COOPERATOR_ID FROM (SELECT ACCOUNTS_ED_DT FROM RD_WEEK_INFO WHERE FIX_DAY IS NOT NULL GROUP BY ACCOUNTS_ED_DT desc LIMIT 7) sub_tab -- 최근 7주만 조회 LEFT JOIN RD_COOPERATOR b ON b.USE_AT ='Y' ) AS fix LEFT JOIN RD_WEEK_INFO a ON fix.COOPERATOR_ID = a.COOPERATOR_ID AND fix.ACCOUNTS_ED_DT = a.ACCOUNTS_ED_DT AND a.FIX_DAY IS NOT NULL LEFT JOIN RD_WEEK_RIDER_INFO b ON a.WEEK_ID = b.WEEK_ID LEFT JOIN RD_COOPERATOR c ON fix.COOPERATOR_ID = c.COOPERATOR_ID where 1 = 1
/* 협력사 조건 */ AND c.REGISTRATION_SN = #{schIhidNum} AND c.use_at ='Y'
GROUP BY fix.ACCOUNTS_ED_DT, fix.COOPERATOR_ID ORDER BY fix.ACCOUNTS_ED_DT
